mapper.update:
UPDATE UME_ROLE
UPDATE_DATETIME = current_timestamp(), ROLE_NAME = #{roleName}, ROLE_DESC = #{roleDesc}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor},
WHERE ROLE_ID = #{roleId} AND UPDATE_DATETIME = #{updateDatetime}

UPDATE UME_ROLE

  UPDATE_DATETIME = current_timestamp(),
  ROLE_NAME = #{roleName},
  ROLE_DESC = #{roleDesc},
  CREATE_AUTHOR = #{createAuthor},
  CREATE_DATETIME = #{createDatetime},
  UPDATE_AUTHOR = #{updateAuthor},

WHERE 1 = 1
  AND ROLE_ID = #{roleId}
  AND ROLE_NAME = #{roleName}
  AND ROLE_DESC = #{roleDesc}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME = #{createDatetime}
  AND UPDATE_DATETIME = #{updateDatetime}

UPDATE UME_ROLE
SET
  ROLE_NAME = #{roleName}
  ,ROLE_DESC = #{roleDesc}
  ,CREATE_AUTHOR = #{createAuthor}
  ,CREATE_DATETIME = #{createDatetime}
  ,UPDATE_AUTHOR = #{updateAuthor}
  ,UPDATE_DATETIME = current_timestamp() 
WHERE 
   ROLE_ID = #{roleId}
   AND UPDATE_DATETIME = #{updateDatetime}

mapper.delete:
DELETE FROM UME_ROLE
WHERE
   ROLE_ID = #{roleId}
   AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  ROLE_ID AS "roleId"
  ,ROLE_NAME AS "roleName"
  ,ROLE_DESC AS "roleDesc"
  ,CREATE_AUTHOR AS "createAuthor"
  ,CREATE_DATETIME AS "createDatetime"
  ,UPDATE_AUTHOR AS "updateAuthor"
  ,UPDATE_DATETIME AS "updateDatetime"
FROM UME_ROLE
WHERE 1 = 1 
  AND ROLE_ID = #{roleId}
  AND ROLE_NAME = #{roleName}
  AND ROLE_DESC = #{roleDesc}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
   ORDER BY ${theOrderByCondition}
   GROUP BY ${theGroupByCondition}
  LIMIT ${theFetchStart}, ${theFetchSize}

SELECT count(*)
FROM UME_ROLE
WHERE 1 = 1 
  AND ROLE_ID = #{roleId}
  AND ROLE_NAME = #{roleName}
  AND ROLE_DESC = #{roleDesc}
