mapper.update:
UPDATE UME_ROLE_ACL

  UPDATE_DATETIME = current_timestamp(),
  CREATE_AUTHOR = #{createAuthor},
  CREATE_DATETIME = #{createDatetime},
  UPDATE_AUTHOR = #{updateAuthor},

WHERE 
   ROLE_ID = #{roleId}
   AND ACC_RES_ID = #{accResId}
   AND ACC_RES_TYPE = #{accResType}
   AND ACC_LEVEL = #{accLevel}
   AND UPDATE_DATETIME = #{updateDatetime}

UPDATE UME_ROLE_ACL

  UPDATE_DATETIME = current_timestamp(),
  CREATE_AUTHOR = #{createAuthor},
  CREATE_DATETIME = #{createDatetime},
  UPDATE_AUTHOR = #{updateAuthor},

WHERE 1 = 1
  AND ROLE_ID = #{roleId}
  AND ACC_RES_ID = #{accResId}
  AND ACC_RES_TYPE = #{accResType}
  AND ACC_LEVEL = #{accLevel}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME = #{createDatetime}
  AND UPDATE_DATETIME = #{updateDatetime}

UPDATE UME_ROLE_ACL
SET
  ,CREATE_AUTHOR = #{createAuthor}
  ,CREATE_DATETIME = #{createDatetime}
  ,UPDATE_AUTHOR = #{updateAuthor}
  ,UPDATE_DATETIME = current_timestamp() 
WHERE 
   ROLE_ID = #{roleId}
   AND ACC_RES_ID = #{accResId}
   AND ACC_RES_TYPE = #{accResType}
   AND ACC_LEVEL = #{accLevel}
   AND UPDATE_DATETIME = #{updateDatetime}

mapper.delete:
DELETE FROM UME_ROLE_ACL
WHERE
   ROLE_ID = #{roleId}
   AND ACC_RES_ID = #{accResId}
   AND ACC_RES_TYPE = #{accResType}
   AND ACC_LEVEL = #{accLevel}
   AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  ROLE_ID AS "roleId"
  ,ACC_RES_ID AS "accResId"
  ,ACC_RES_TYPE AS "accResType"
  ,ACC_LEVEL AS "accLevel"
  ,CREATE_AUTHOR AS "createAuthor"
  ,CREATE_DATETIME AS "createDatetime"
  ,UPDATE_AUTHOR AS "updateAuthor"
  ,UPDATE_DATETIME AS "updateDatetime"
FROM UME_ROLE_ACL
WHERE 1 = 1 
  AND ROLE_ID = #{roleId}
  AND ACC_RES_ID = #{accResId}
  AND ACC_RES_TYPE = #{accResType}
  AND ACC_LEVEL = #{accLevel}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
   ORDER BY ${theOrderByCondition}
   GROUP BY ${theGroupByCondition}
  LIMIT ${theFetchStart}, ${theFetchSize}

SELECT count(*)
FROM UME_ROLE_ACL
WHERE 1 = 1 
  AND ROLE_ID = #{roleId}
  AND ACC_RES_ID = #{accResId}
  AND ACC_RES_TYPE = #{accResType}
  AND ACC_LEVEL = #{accLevel}
