mapper.update:
UPDATE UME_USER
UPDATE_DATETIME = current_timestamp(), USER_PASSWORD = #{userPassword}, USER_NAME = #{userName}, USER_NICK_NAME = #{userNickName}, USER_GENDER = #{userGender}, USER_MOBILE = #{userMobile}, USER_EMAIL = #{userEmail}, USER_HEAD_IMAGE = #{userHeadImage}, USER_DESC = #{userDesc}, USER_STATUS = #{userStatus}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor},
WHERE USER_ID = #{userId} AND UPDATE_DATETIME = #{updateDatetime}

UPDATE UME_USER

  UPDATE_DATETIME = current_timestamp(),
  USER_PASSWORD = #{userPassword},
  USER_NAME = #{userName},
  USER_NICK_NAME = #{userNickName},
  USER_GENDER = #{userGender},
  USER_MOBILE = #{userMobile},
  USER_EMAIL = #{userEmail},
  USER_HEAD_IMAGE = #{userHeadImage},
  USER_DESC = #{userDesc},
  USER_STATUS = #{userStatus},
  CREATE_AUTHOR = #{createAuthor},
  CREATE_DATETIME = #{createDatetime},
  UPDATE_AUTHOR = #{updateAuthor},

WHERE 1 = 1
  AND USER_ID = #{userId}
  AND USER_PASSWORD = #{userPassword}
  AND USER_NAME = #{userName}
  AND USER_NICK_NAME = #{userNickName}
  AND USER_GENDER = #{userGender}
  AND USER_MOBILE = #{userMobile}
  AND USER_EMAIL = #{userEmail}
  AND USER_HEAD_IMAGE = #{userHeadImage}
  AND USER_DESC = #{userDesc}
  AND USER_STATUS = #{userStatus}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME = #{createDatetime}
  AND UPDATE_DATETIME = #{updateDatetime}

UPDATE UME_USER
SET
  USER_PASSWORD = #{userPassword}
  ,USER_NAME = #{userName}
  ,USER_NICK_NAME = #{userNickName}
  ,USER_GENDER = #{userGender}
  ,USER_MOBILE = #{userMobile}
  ,USER_EMAIL = #{userEmail}
  ,USER_HEAD_IMAGE = #{userHeadImage}
  ,USER_DESC = #{userDesc}
  ,USER_STATUS = #{userStatus}
  ,CREATE_AUTHOR = #{createAuthor}
  ,CREATE_DATETIME = #{createDatetime}
  ,UPDATE_AUTHOR = #{updateAuthor}
  ,UPDATE_DATETIME = current_timestamp() 
WHERE 
   USER_ID = #{userId}
   AND UPDATE_DATETIME = #{updateDatetime}

mapper.delete:
DELETE FROM UME_USER
WHERE
   USER_ID = #{userId}
   AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  USER_ID AS "userId"
  ,USER_PASSWORD AS "userPassword"
  ,USER_NAME AS "userName"
  ,USER_NICK_NAME AS "userNickName"
  ,USER_GENDER AS "userGender"
  ,USER_MOBILE AS "userMobile"
  ,USER_EMAIL AS "userEmail"
  ,USER_HEAD_IMAGE AS "userHeadImage"
  ,USER_DESC AS "userDesc"
  ,USER_STATUS AS "userStatus"
  ,CREATE_AUTHOR AS "createAuthor"
  ,CREATE_DATETIME AS "createDatetime"
  ,UPDATE_AUTHOR AS "updateAuthor"
  ,UPDATE_DATETIME AS "updateDatetime"
FROM UME_USER
WHERE 1 = 1 
  AND USER_ID = #{userId}
  AND USER_PASSWORD = #{userPassword}
  AND USER_NAME = #{userName}
  AND USER_NICK_NAME = #{userNickName}
  AND USER_GENDER = #{userGender}
  AND USER_MOBILE = #{userMobile}
  AND USER_EMAIL = #{userEmail}
  AND USER_HEAD_IMAGE = #{userHeadImage}
  AND USER_DESC = #{userDesc}
  AND USER_STATUS = #{userStatus}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
   ORDER BY ${theOrderByCondition}
   GROUP BY ${theGroupByCondition}
  LIMIT ${theFetchStart}, ${theFetchSize}

SELECT count(*)
FROM UME_USER
WHERE 1 = 1 
  AND USER_ID = #{userId}
  AND USER_PASSWORD = #{userPassword}
  AND USER_NAME = #{userName}
  AND USER_NICK_NAME = #{userNickName}
  AND USER_GENDER = #{userGender}
  AND USER_MOBILE = #{userMobile}
  AND USER_EMAIL = #{userEmail}
  AND USER_HEAD_IMAGE = #{userHeadImage}
  AND USER_DESC = #{userDesc}
  AND USER_STATUS = #{userStatus}
